            if np.random.rand() < 0.05:
                enemy_units.append({"x": x, "y": y, "type": "infantry"})
    elif sensor == "optical":
        # Detect enemy units within optical range
        for i in range(int(data["range"] / data["resolution"])):
            x = data["x"] + i * data["resolution"]
            y = data["y"]
            if np.random.rand() < 0.01:
