Interceptor = {
    "target": None,
    "wrappees": {}
}

def intercept(func):
    def wrapper(*args, **kwargs):
        # Intercept the function call
        if func not in Interceptor["wrappees"]:
            Interceptor["wrappees"][func] = []
        Interceptor["wrappees"][func].append((args, kwargs))

        # Call the original function
        result = func(*args, **kwargs)

        return result
    return wrapper

class NetworkTraffic:
